const nav=document.querySelector('nav');const navBtn=document.querySelector('.hamburger');const allNavItems=document.querySelectorAll('.nav-item');const footerYear=document.querySelector('.year');const handleNav=()=>{nav.classList.toggle('active');navBtn.classList.toggle('is-active');handleNavItemsAnimation();allNavItems.forEach(item=>{item.addEventListener('click',()=>{nav.classList.remove('active');navBtn.classList.remove('is-active');item.classList.remove('nav-items-animation');});});};navBtn.addEventListener('click',handleNav);const handleNavItemsAnimation=()=>{let delayTime=0;allNavItems.forEach(item=>{item.classList.toggle('nav-items-animation');item.style.animationDelay='.'+delayTime+'s';delayTime++;});};const handleCurrentYear=()=>{const year=new Date().getFullYear();footerYear.innerText=year;};handleCurrentYear();const navItemsDelete=()=>{allNavItems.forEach(item=>{if(item.classList.contains('nav-items-animation')){item.classList.remove('nav-items-animation');}});};allNavItems.forEach(item=>{item.addEventListener('click',navItemsDelete);});window.addEventListener('wheel',a=>{if(a.ctrlKey===true)a.preventDefault();},{passive:false});const nameForm=document.querySelector('#name_form_input');const emailForm=document.querySelector('#email_form_input');const textAreaForm=document.querySelector('.contact-form-textarea');const formBtn=document.querySelector('.contact-form-btn');const checkName=()=>{if(nameForm.value<5||nameForm.value==='Wpisz Swoje imię i nazwisko!!'){nameForm.value='Wpisz Swoje imię i nazwisko!!';}else{nameForm.value='';}};const checkEmail=()=>{if(emailForm.value.indexOf('@')===-1){emailForm.value='Wpisz poprawny Adres Email!!';}else{emailForm.value='';}};const checkTextArea=()=>{if(textAreaForm.value<15||textAreaForm.value==='Wpisz treść wiadomości!!'){textAreaForm.value='Wpisz treść wiadomości!!';}else{textAreaForm.value='';}};formBtn.addEventListener('click',e=>{e.preventDefault();checkName();checkEmail();checkTextArea();});